from matplotlib import image
import streamlit as st
import pandas as pd

#d이미지 처리를 위한 라이브러리
from PIL import Image

def main():
    #pass
    #로컬에 저장되어 있는 이미지 파일을 화면에 표시하기
    img=Image.open('data2/image_03.jpg')
    #print(img)
    st.image(img,'곰돌이')
    st.image(img,'곰돌이', use_column_width=True)   #가로 전체

    #인터넷상(URL)에 있는 이미지를 화면에 표시하기
    url='data:image/jpeg;base64,'
    st.image(url)

    #동영상 파일을 화면에 표시하기
    video_file=open('data2/secret_of_success.mp4','rb')
    st.video(video_file)

    #오디오 파일을 화면에 표시하기
    audio_file=open('data2/song.mp3', 'rb')
    st.audio(audio_file.read(),format='audio/mp3')

if __name__=='__main__':
    main()